import React, { Component } from 'react';
import styles from './Table.module.css';
import TableRow from '../TableRow';

class Table extends Component{
    constructor(props){
        super(props);
        this.state = {
            tableItems : []
        }
    }
    componentDidMount(){
        fetch('/api/alldreans')
            .then(res => res.json())
            .then(res => {
                this.setState({tableItems : res.dreans},()=>{
                    console.log("Table state : ",this.state.tableItems);
                });
            })
    }
    render(){
        // console.log("test : ",this.state.tableItems[0].id);
        const items = this.state.tableItems;
        console.log("Items [0] : ", items.length > 0 && items[0].id);
        return(
            <div>
                <h1>Table</h1>
                <TableRow isHead={true}/>
                {items.length > 0 ? 
                <div>
                    <TableRow isHead={false} codeName={items[0].codeName} description='Make some cool' date='22.04.2023'/>
                    <TableRow isHead={false} codeName={items[1].codeName} description='Meet with Pikachy' date='12.11.2021'/>
                </div>
                :
                 <h1>Empty Table</h1>
                }
            </div>
        )
    }
}

export default Table;
