import { withRouter } from 'next/router'
import { Component } from 'react'
import Layout from '../../Layout';
import EditorField from '../../components/EditorField';

class Redact extends Component {
    constructor(props) {
        super(props);
        this.state = {
            item : {}
        }
    }

    componentDidMount() {
        const { router: { query }} = this.props;
        if (query.id) {
            fetch(`/api/redact/${query.id}`)
                .then(res => res.json())
                .then(res => {
                    this.setState({ item : res })
                })
        }
    }

    render() {
        const element = this.state.item;
        let redact_item = null; 
        if(element){
            console.log('elem code name O ',element.codeName);
        redact_item = 
            <div> 
                <h1> ID : {element.id}</h1>
                <EditorField pro={element.codeName}/>
            </div>
        }else{
            redact_item = <div>Empty</div>
        }
        return (
            <Layout>
                <div>
                    Redact
                    {redact_item}

                    <TextField text='ssd'/>
                </div>
            </Layout>
        )
    }
}
class TextField extends Component{
    constructor(props){
        super(props);
        this.state = {
            text : this.props.text
        }
    }

    render(){
        return(
            <div>
                <h1>Text Area</h1>
                <textarea value={this.state.text} onChange={(event) => this.handlerOnChange(event)}/>
            </div>
        )
    }

    handlerOnChange(event){
        console.log('target : ',event.target.value);
        this.setState({text : event.target.value});
    }
}

export default withRouter(Redact)
