import React, { Component } from 'react';
import Layout from '../Layout/layout'

const API = 'https://localhost:3000/api/';
const DEFAULT_QUERY = 'items';

class Home extends Component {
    componentDidMount() {
        fetch('/')
            .then(response => response.json())
            .then(data => this.setState({ data }));
    }

    render() {
        let items = 'Loading . . .';
        if (this.state.data) {
            items = this.state.data.map(item => {
                return <li>{item}</li>
            })
        }
        return (
            <Layout>
                <div>
                    <form method="post">
                        <input type="text" />
                        <button type="submit">get</button>
                    </form>
                    <h1>Recepies</h1>
                    <ul>
                        {items}
                    </ul>
                </div>
            </Layout>
        )
    }
}

export default Home;
