import React, { Component } from 'react';
import Layout from '../Layout/layout'

const API = 'http://localhost:3000/api';
const DEFAULT_QUERY = '/items';

class Home extends Component {
    constructor(props){
        super(props);
        this.state = {
            users : []
        }
    }

    componentDidMount() {
        this.setState({ isLoading: true });

        fetch('/api/items')
            .then(res => {
                console.log(res);
                return res.json();
             })
            .then(items => {
                console.log("ITEMS : "+items);
                this.setState(items)
             });
    }



    render() {
        console.log("render users"+this.state.users[0])
        let items='Items';
        if (this.state.users) {
            items = this.state.users.map(item => {
                return <li>{item.name}</li>
            })
        }else{
            items = 'Loading . . .';
        }
        return (
            <Layout>
                <div>
                    <form method="post">
                        <input type="text" />
                        <button type="submit">Get</button>
                    </form>
                    <h1>Recepies</h1>
                    <ul>
                        {items}
                    </ul>
                </div>
            </Layout>
        )
    }
}

export default Home;
