import { Component } from "react";

class RedactForm extends Component {

    render() {
        const element = this.props.value;
        console.log(element);

        return (
            <form>
                <h1> ID : {element.id}</h1>


                {/* <input type="text" name="codeName" value={element.codeName} onChange={(event) => this.handleInputChange()} /> */}
                {/* <input type="text" name="description" value={element.description} onChange={(event) => this.handleInputChange()} /> */}

                <button onClick={() => this.handleSave()}>Save</button>
            </form>
        )
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.setState({
            [name]: value
        });
    };

    handleSave() {
        console.log("Click save!");
        console.log("this Item : ", this.state.item);
        console.log('this state codeName : ', this.state.codeName);
    }
}

export default RedactForm;
