import { Component } from "react";

class RedactForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            item : {}
            // id: this.props.data.id,
            // codeName: this.props.data.codeName,
            // description: this.props.data.description,
            // date: this.props.data.date
        }
    }

    render() {
        const element = this.state.item;
        return (
            <form onSubmit={this.handleOnSubmit}>
                <h1> ID : {element.id}</h1>
                <input type="hidden" name="id" value={element.id} />
                <input type="text" name="codeName" value={element.codeName} onChange={this.handleInputChange} />
                <input type="text" name="description" value={element.description} onChange={this.handleInputChange} />
                <input type="text" name="date" value={element.date} onChange={this.handleInputChange} />

                <button type="submit">Save</button>
            </form>
        )
    }

    handleOnSubmit = (event) => {
        event.preventDefault();
        this.props.onSubmit(this.state);
    }

    handleInputChange = (event) => {
        const value = event.target.value;
        const name = event.target.name;
        this.setState({
            [name]: value
        });
    };
}

export default RedactForm;
