import { withRouter } from 'next/router'
import { Component } from 'react'
import Layout from '../../Layout';
import TextField from '../../components/EditorField';
import Link from 'next/link';

class Redact extends Component {
    constructor(props) {
        super(props);
        this.state = {
            item: {}
        }
    }

    componentDidMount() {
        const { router: { query } } = this.props;
        if (query.id) {
            fetch(`/api/redact/${query.id}`)
                .then(res => res.json())
                .then(res => {
                    this.setState({ item: res })
                })
        }
    }

    render() {
        const element = this.state.item;
        let redact_item = null;
        if (element.id) {
            console.log('elem code name O ', element.codeName);
            redact_item =
                <form>
                    <h1> ID : {element.id}</h1>
                    <TextField text={element.codeName} parent={this} fieldType='codeName'/>
                    <TextField text={element.description} parent={this} fieldType='description'/>
                    <TextField text={element.date}/>

                    <input type="text" value={element.codeName}/>
                </form>
        } else {
            redact_item = <div>Empty</div>
        }
        return (
            <Layout>
                <div>
                    Redact
                    {redact_item}


                </div>
            </Layout>
        )
    }

    newValueOfField(children){
        console.log("field type : ",children.state.fieldType);
        console.log("new value of field : ",children.state.text);
    }
}

export default withRouter(Redact)
