import React, { Component } from 'react';
import styles from './Table.module.css';
import TableRow from '../TableRow';

class Table extends Component {
    constructor(props){
        super(props);
        this.state = {
            tableItems : []
        }
    }
    componentDidMount(){
        fetch('/api/alldreans')
            .then(res => res.json())
            .then(res => {
                this.setState({tableItems : res.dreans},()=>{
                    console.log("Table state : ",this.state.tableItems);
                });
            })
    }

    render() {
        const items = this.state.tableItems;
        console.log('table render',items);
        const rows = items.map(item =>{
            <TableRow codeName={item.codeName} description={items.description} date={item.date}/>
        })
        return (
            <div>
                <table>
                    <thead>
                        <tr>
                            <th>Code Name</th>
                            <th>Description</th>
                            <th>Date</th>
                            <th>Guests</th>
                            <th>Need Things</th>
                            <th>Activity</th>
                        </tr>
                    </thead>
                    {items.length>0?
                    <div>
                        {rows}
                    </div>
                    :
                    <div>
                        <h1>Table is empty!</h1>
                    </div>}
                </table>
            </div>
        )
    }
}

export default Table;
