import React, { Component } from 'react';

class ItemsList extends Component{
    constructor(props){
        super(props);
        this.state = {
            items : [],
            isOpen : false
        }
    }

    render(){
        let itemsList = null;
        if(!this.state.isOpen){
            itemsList = [];
        }else{
            itemsList = this.state.items.map(item => <li key={item.id}>{item.value}</li>)
        }
        return (
            <div>
                <h1>Header of List : {this.props.itemsListHeader}</h1>
                <ul>
                    {itemsList}
                </ul>
            </div>
        )
    }
}
