import { Component } from "react";

class RedactForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            id : this.props.data.id,
            codeName : this.props.data.codeName,
            description : this.props.data.description,
            date : this.props.data.date
        }
    }
    render() {
        const element = this.state;
        return (
            <form>
                <h1> ID : {element.id}</h1>

                <input type="text" name="codeName" value={element.codeName} onChange={(event) => this.handleInputChange(event)} />
                <input type="text" name="description" value={element.description} onChange={(event) => this.handleInputChange(event)} />

                <button onClick={() => this.handleSave()}>Save</button>
            </form>
        )
    }

    handleInputChange(event) {
        const value = event.target.value;
        const name = event.target.name;
        this.setState({
            [name]: value
        });
    };

    handleSave() {
        console.log("Click save!");
        console.log("this Item : ", this.state.item);
        console.log('this state codeName : ', this.state.codeName);
    }
}

export default RedactForm;
