import { withRouter } from 'next/router'
import { Component } from 'react'
import Layout from '../../Layout';

class Redact extends Component {
    constructor(props) {
        super(props);
        this.state = {
            item : {}
        }
    }

    componentDidMount() {
        const { router: { query }} = this.props;
        if (query.id) {
            fetch(`/api/redact/${query.id}`)
                .then(res => res.json())
                .then(res => {
                    this.setState({ item : res })
                })
        }
    }

    render() {
        const element = this.state.item;
        let redact_item = null; 
        if(element){
            console.log('elem code name O ',element.codeName);
        redact_item = 
            <div> 
                <h1> ID : {element.id}</h1>
                <Editor value={element.codeName}/>
            </div>
        }else{
            redact_item = <div>Empty</div>
        }
        return (
            <Layout>
                <div>
                    Redact
                    {redact_item}
                </div>
            </Layout>
        )
    }
}

class Editor extends React.Component {
    constructor(props) {
      super(props)
      console.log('props : editor : value ',props.value);
      console.log('props : editor : ',props);
      this.state = {
        textareaValue: props.value
      }
    }
    handleOnChange(event) {
      this.setState({
        textareaValue: event.target.value
      })
    }

    render() {
      return(
          <div>
            <form onSubmit={(event) => this.handleOnSubmit(event)}>
                <textarea value={this.state.textareaValue} 
                onChange={(event) => this.handleOnChange(event)}></textarea>
            </form>
        </div>
      ) 
    }
  }

export default withRouter(Redact)
