import React, { Component } from 'react';
import Layout from '../Layout'
import Table from '../components/Table';

class Dreans extends Component {
    constructor(props) {
        super(props);
        this.state = {
            tableItems: []
        }
    }

    getData = () => {
        fetch('/api/alldreans')
            .then(res => res.json())
            .then(res => {
                this.setState({ tableItems: res }, () => {
                    console.log('dreans init', this.state.tableItems);
                });
            })
    }



    componentDidMount() {
        console.log('did mount dreans')
        this.getData();
    }

    render() {
        console.log('render dreans')
        return (
            <Layout>
                <Table data={this.state.tableItems} />
                <button onClick={this.getData} />
            </Layout>
        )
    }
}

export default Dreans;
