import React, { Component } from 'react';
import Layout from '../Layout/layout'

const API = 'http://localhost:3000/api';
const DEFAULT_QUERY = '/items';

class Home extends Component {
    constructor(props){
        super(props);
        this.state = {
            items : [],
            isLoading: false
        }
    }

    componentDidMount() {
        this.setState({ isLoading: true });

        fetch(API+DEFAULT_QUERY)
            .then(res => {
                console.log(res);
                return res.json();
             })
            .then(items => {
                console.log(items);
                this.setState({ items : items.data})
             });
    }



    render() {
        let items='Items';
        // if (this.state.isLoading) {
        //     items = this.state.data.map(item => {
        //         return <li>{item}</li>
        //     })
        // }else{
        //     items = 'Loading . . .';
        // }
        return (
            <Layout>
                <div>
                    <form method="post">
                        <input type="text" />
                        <button type="submit">get</button>
                    </form>
                    <h1>Recepies</h1>
                    <ul>
                        {items}
                    </ul>
                </div>
            </Layout>
        )
    }
}

export default Home;
