import Layout from '../Layout/layout'

const API = 'http://localhost:3000/api';
const DEFAULT_QUERY = '/items';

class Home extends Component {
    state = {
        isLoading: false,
    };

    componentDidMount() {
        this.setState({ isLoading: true });

        fetch(API+DEFAULT_QUERY)
            .then(res => {
                return res.json()
             })
            .then(items => {  
                this.setState({ items })
             });
    }



