import { withRouter } from 'next/router'
import { Component } from 'react'
import Layout from '../../Layout';
import TextField from '../../components/EditorField';
import Link from 'next/link';

class Redact extends Component {
    constructor(props) {
        super(props);
        this.state = {
            item: {},
            codeName : this.codeName.getData(),
            description : this.description.getData()
        }
    }

    componentDidMount() {
        const { router: { query } } = this.props;
        if (query.id) {
            fetch(`/api/redact/${query.id}`)
                .then(res => res.json())
                .then(res => {
                    this.setState({ item: res })
                })
        }
    }

    render() {
        const element = this.state.item;
        let redact_item = null;
        if (element.id) {
            console.log('elem code name O ', element.codeName);
            redact_item =
                <div>
                    <h1> ID : {element.id}</h1>
                    <TextField text={element.codeName} parent={this}/>
                    <TextField text={element.description} parent={this}/>
                    <TextField text={element.date}/>
                </div>
        } else {
            redact_item = <div>Empty</div>
        }
        return (
            <Layout>
                <div>
                    Redact
                    {redact_item}

                    <button onClick={()=>this.handleSave()}>Save</button>
                </div>
            </Layout>
        )
    }

    handleSave(){
        console.log("Click save!");
        console.log("this Item : ",this.state.item);
        console.log('this state codeName : ',this.state.codeName);
    }
}

export default withRouter(Redact)
