import { withRouter } from 'next/router'
import { Component } from 'react'
import Layout from '../../Layout';

class Redact extends Component {
    constructor(props) {
        super(props);
        this.state = {
            item : {}
        }
    }

    componentDidMount() {
        const { router: { query }} = this.props;
        if (query.id) {
            fetch(`/api/redact/${query.id}`)
                .then(res => res.json())
                .then(res => {
                    this.setState({ item : res })
                })
        }
    }

    render() {
        const element = this.state.item;
        let redact_item = null; 
        if(element){
        redact_item = 
            <div> 
                <h1> ID : {element.id}</h1>
                <textarea>{element.codeName}</textarea>
                <input type="text" text={element.codeName}></input>
            </div>
        }else{
            redact_item = <div>Empty</div>
        }
        return (
            <Layout>
                <div>
                    Redact
                    {redact_item}
                </div>
            </Layout>
        )
    }
}

export default withRouter(Redact)
